  }
  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={handleChange} type="text" value={val}/>
        <button onClick={handleClick}>
          <span>Add</span>
        </button>
      </div>
      <div>
